insert into sources (name, id_out, publication_year, about_source, user_id, verification_status, verified_user_id, "image_URL") select 'Психология влияния' , 'korvintag_470' , '1984' , ' научно-популярная книга американского психолога Роберта Чалдини. Он получил известность не только благодаря своей книге «Психология влияния» (1984), но и научным работам «Дверь в лицо» (1975) о так называемой манипулятивной технике. Изучал межличностные отношения, психологию влияния и уступчивости. Выяснил действие механизмов просьб и требований, которые он назвал «орудиями влияния». Для наблюдения за реальными ситуациями Чалдини три года «под прикрытием» проходил обучение в различных центрах. Он обнаружил, что влияние основано на шести ключевых принципах: взаимность, приверженность и последовательность, социальное доказательство, авторитет, симпатия, дефицит. В 2016 году он предложил седьмой принцип — единство. В своей книге Чалдини описывает, как распознать основные манипуляции с нашим сознанием и какие способы защиты от них существуют.', (select id from users where id_out='korvintag_24'),3 , (select id from users where name='korvin'),'source_from_korvintag_470.jpg'  where not exists (select * from sources where id_out='korvintag_470');
