insert into sources (name, id_out, publication_year, about_source, user_id, verification_status, verified_user_id, "image_URL") select 'Атомные привычки. Как приобрести хорошие привычки и избавиться от плохих' , 'korvintag_472' , '2018' , 'Может ли одна монетка сделать человека богатым? Конечно, нет, скажете вы. Но если добавить еще одну? И еще? И еще? В какой-то момент количество перейдет в качество. А теперь представьте, что одно крошечное изменение кардинально меняет всю жизнь. Звучит фантастически! Но, как и в случае с монетками, срабатывает эффект сложного процента. И вот уже маленькое, но регулярное действие привело к большим результатам. Атомные привычки – маленькие изменения, в которых скрыта огромная мощь! Вы давно пытаетесь измениться, но не получается. Не корите себя! Виноваты не вы, а система. Джеймс Клир развенчивает мифы о привычках, мотивации и силе воли. Опираясь на научные данные, результаты исследований, личный опыт и опыт успешных людей, он просто и понятно объясняет, как сделать хорошие привычки неизбежными, а вредные – невозможными. Без вариантов!', (select id from users where id_out='korvintag_24'),3 , (select id from users where name='korvin'),'source_from_korvintag_472.jpg'  where not exists (select * from sources where id_out='korvintag_472');
