insert into ideas (name, id_out, original_text, content, user_id, verification_status, verified_user_id, date_time_create,"SVG") select 'Серьезность и усталость' , 'korvintag_2252' , 'Ты единственный, которого я видел играющим с Мескалито. Ты не привык к такой жизни. Поэтому указания (omens) прошли мимо тебя. Да, ты серьезный человек, но твоя серьезность привязана к тому, что ты делаешь, а не к тому, что происходит вне тебя. Ты слишком застреваешь на самом себе. В этом беда. И это порождает ужасную усталость.

Ищи чудес повсюду вокруг себя и смотри на них. Ты устаешь от глядения на одного себя, и эта усталость делает тебя глухим и слепым ко всему остальному.
— Ты попал в точку, дон Хуан. Но как мне измениться?' , 'Серьезность не должна быть привязана только к тому, что мы делаем. Серьезно нужно относиться и к тому, что происходит вне нас. Односторонняя серьезность порождает сильную усталость. Именно усталость не дает нам замечать чудеса.', (select id from users where id_out='korvintag_6'), 3, (select id from users where name='korvin'), to_timestamp( '11.08.2023 11:14:21', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 337.074 337.073" style="enable-background:new 0 0 337.074 337.073;" xml:space="preserve"><g><path d="M168.542,0C75.616,0,0,75.604,0,168.543c0,92.93,75.616,168.53,168.542,168.53c92.931,0,168.531-75.601,168.531-168.53 C337.074,75.604,261.473,0,168.542,0z M206.649,135.997c8.983,0,16.268,7.292,16.268,16.27c0,8.978-7.296,16.271-16.268,16.271 c-8.982,0-16.272-7.293-16.272-16.271C190.376,143.29,197.667,135.997,206.649,135.997z M130.43,135.997 c8.998,0,16.27,7.292,16.27,16.27c0,8.978-7.278,16.271-16.27,16.271c-8.989,0-16.27-7.293-16.27-16.271 C114.16,143.29,121.435,135.997,130.43,135.997z M252.057,234.121H85.016c-6.801,0-12.322-5.519-12.322-12.334 c0-6.791,5.515-12.34,12.322-12.34h167.041c6.822,0,12.328,5.537,12.328,12.34C264.379,228.603,258.879,234.121,252.057,234.121z"/></g></svg>' where not exists (select * from ideas where id_out='korvintag_2252');
