insert into ideas (name, id_out, original_text, content, user_id, verification_status, verified_user_id, date_time_create,"SVG") select 'Страх - первый природный враг' , 'korvintag_2259' , '— Когда человек начинает учиться, он никогда не знает ясно своих целей. Его замысел ошибочен, а намерение смутно. Он надеется на вознаграждения, которых никогда не получит, потому что еще ничего не знает о тяготах учения.
Он медленно начинает учиться — сначала понемногу, шаг за шагом, а потом большими скачками. И скоро у него ум зайдет за разум. То, что он узнает, всегда оказывается не тем, что он себе рисовал или воображал, и поэтому он начинает пугаться. Учение никогда не даст того, чего от него ожидают. Каждый шаг — это новая задача, и страх, испытываемый человеком, накапливается безжалостно и неуклонно. Замысел оказывается полем битвы.
И таким образом он натыкается на первого из своих природных врагов. Страх! Ужасный враг — вероломный и трудноодолимый. Он таится на каждом повороте пути, маскируется, выжидает. И если человек, испугавшись непосредственной близости страха, побежит прочь, враг положит конец его поискам.' , 'Страх - первый неминуемый враг на пути к знанию. Если испугаться страха и побежать прочь, то придет конец всех поисков.', (select id from users where id_out='korvintag_6'), 3, (select id from users where name='korvin'), to_timestamp( '11.08.2023 16:32:42', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 856.6 856.6" style="enable-background:new 0 0 856.6 856.6;" xml:space="preserve"><g><path d="M304.199,752.7c-10.5-4.4-19.399-12.3-25-22.8l-28.5-53.7c-1.8-3.4-4.5-6.2-7.899-8.101c-3.4-1.899-6.8-3.899-10.2-5.899 c-13.4-8-30.3,1.6-30.3,17.1v78c0,2.7,1.1,5.4,3.1,7.3l91.101,86.5c3.699,3.5,8.6,5.5,13.8,5.5h118h118c5.1,0,10.1-2,13.8-5.5 l91.1-86.5c2-1.899,3.101-4.5,3.101-7.3v-78c0-15.5-16.9-25.1-30.3-17.1c-3.4,2-6.801,4-10.2,5.899c-3.4,1.9-6.101,4.7-7.9,8.101 l-28.5,53.7c-5.5,10.5-14.5,18.399-25,22.8C544.899,755.8,540,763.1,540,771.2V788c0,8.6-5.4,16.4-13.601,19 c-13.7,4.2-26.399-5.9-26.399-19v-11.5c0-11-9-20-20-20h-11.7c-11,0-20,9-20,20V788c0,8.6-5.4,16.4-13.601,19 c-2.2,0.7-4.3,1-6.399,1c-2.101,0-4.2-0.3-6.4-1c-8.2-2.5-13.6-10.4-13.6-19v-11.6c0-11-9-20-20-20h-11.7c-11,0-20,9-20,20V788 c0,13.1-12.601,23.3-26.4,19c-8.2-2.5-13.6-10.4-13.6-19v-16.9C316.6,763,311.699,755.8,304.199,752.7z"/><path d="M771.3,343c0-189.4-153.601-343-343-343c-189.4,0-343,153.6-343,343c0,120.7,62.4,226.8,156.6,288 c9.601,6.3,19.601,12,29.9,17.3c3.6,1.9,6.6,4.8,8.5,8.4l34.2,64.5c1.699,3.3,5.1,5.3,8.8,5.3h15.8c11,0,20-9,20-20v-12.2 c0-13.1,12.6-23.3,26.4-19c8.199,2.5,13.6,10.4,13.6,19v12.3c0,11,9,20,20,20h9.2h9.2c11,0,20-9,20-20v-12.3 c0-8.6,5.399-16.