insert into ideas (name, id_out, original_text, content, user_id, verification_status, verified_user_id, date_time_create,"SVG") select 'Важнее не состояние, а тренд изменений' , 'korvintag_4456' , 'Исходя их этого, не очень важно, насколько вы успешны или неуспешны в данный момент. Самое важное – это то, насколько привычки способны привести вас на путь успеха. Вы должны в гораздо большей степени беспокоиться о траектории развития, а не о достигнутых на данный момент результатах. Если вы миллионер, но при этом ваши ежемесячные траты превышают доходы, то вы находитесь на неправильной траектории развития. Если ваши привычки тратить деньги не изменятся, вы придете к плохому результату. И наоборот, если вы изменитесь и начнете каждый месяц экономить даже небольшую сумму, то встанете на путь, который приведет к финансовой свободе, даже если вы будете двигаться медленнее, чем хотелось бы.' , 'Важнее не состояние, которое мы занимаем в жизни здесь и сейчас, а тренд наших изменений. Важнее то, где мы окажемся завтра, через месяц, через год. Нужно выбирать привычки, которые приведут нас на перспективную траекторию нашей жизни. И не стоит слишком много внимания уделять неприятным текущим и ближайшим состояниям на этом пути.', (select id from users where id_out='korvintag_24'), 3, (select id from users where name='korvin'), to_timestamp( '21.02.2024 18:54:53', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><g  id="direction/trend-top-left" ><path id="Fill-196" d="m7.00015 7.4981c0-.276-.224-.5-.5-.5h-3.999c-.268-.006-.508.23-.50114941.502v3.998c0 .276.22414941.5.50014941.5s.5-.224.5-.5v-2.793l6.146 6.147c.196.195.512.195.708 0l4.646-4.647 5.646 5.647c.196.195.512.195.708 0 .195-.196.195-.512 0-.708l-6-6c-.196-.195-.512-.195-.708 0l-4.646 4.647-5.793-5.793h2.793c.276 0 .5-.224.5-.5" /></g></svg>' where not exists (select * from ideas where id_out='korvintag_4456');
