insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Сердиться - признавать важность других людей' , 'korvintag_2257' , '— Ты не сердишься на меня, дон Хуан? — спросил я, когда он вернулся. Его, по-видимому, удивил мой вопрос.
— Нет, я никогда ни на кого не сержусь. Никому из людей не удастся сделать что-то, достаточно для этого важное. На людей сердишься, когда веришь, что их поступки важны. Ничему подобному я больше не верю.' , 'Если ты сердишься на людей, то это значит, что ты веришь, что их поступки важны. А может стоить посомневаться в важности их поступков? Тогда и сердитость исчезнет?', (select id from users where id_out='korvintag_6'), (select id from users where name='korvin'), to_timestamp( '11.08.2023 14:50:33', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 466.024 466.024" style="enable-background:new 0 0 466.024 466.024;" xml:space="preserve"><g><path d="M447.106,266.299c0-8.495-2.517-16.408-6.83-23.053c1.749-10.947,2.645-22.142,2.645-33.333 C442.922,94.167,348.755,0,233.009,0C117.262,0,23.095,94.167,23.095,209.913c0,11.194,0.896,22.392,2.646,33.342 c-4.309,6.643-6.823,14.553-6.823,23.043c0,21.148,15.54,38.731,35.799,41.941c10.811,88.782,86.642,157.785,178.295,157.785 s167.485-69.002,178.295-157.785C431.567,305.03,447.106,287.447,447.106,266.299z M34.918,266.299 c0-11.807,7.772-21.83,18.468-25.23l0,45.331c0,1.725,0.03,3.443,0.079,5.156C42.728,288.181,34.918,278.135,34.918,266.299z M233.012,450.024c-90.224,0-163.625-73.402-163.625-163.625l-0.001-82.724c0-12.358,6.481-23.292,17.337-29.246 c5.06-2.775,10.521-4.156,15.959-4.156c6.116,0,12.204,1.747,17.655,5.222c33.637,21.44,72.599,32.773,112.671,32.773 c40.074,0,79.037-11.333,112.677-32.774c10.297-6.563,22.861-6.962,33.611-1.066c10.856,5.954,17.339,16.887,17.339,29.245 l0.003,82.727C396.638,376.622,323.236,450.024,233.012,450.024z M386.991,160.399c-15.952-8.748-34.607-8.149-49.905,1.603 c-31.066,19.801-67.056,30.267-104.077,30.267c-37.021,0-73.008-10.466-104.072-30.266c-15.299-9.75-33.957-10.352-49.908-1.602 c-16.057,8.807-25.644,24.984-25.643,43.274l0,20.921c-4