— Его нужно победить пониманием. Человек должен прийти к пониманию того, что сила, которую он, казалось бы, покорил, в действительности никогда ему не принадлежала. Он все время должен придерживаться своей линии, обращаясь осторожно и добросовестно со всем, что узнал. Если он сумеет увидеть, что ясность и сила без контроля над самим собой еще хуже, чем ошибки, достигнет такой точки, где все схвачено. Тут он будет знать, когда и как использовать свою силу. И таким образом победит своего третьего врага.' , 'Своеволие силы можно победить лишь пониманием. Того, что сила ему не принадлежит. Нужен самоконтроль!', (select id from users where id_out='korvintag_6'), (select id from users where name='korvin'), to_timestamp( '11.08.2023 20:18:21', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 55.118 55.118" style="enable-background:new 0 0 55.118 55.118;" xml:space="preserve"><g><path d="M49.209,23.533c-0.499-0.177-1.043-0.135-1.499,0.114c-0.429,0.235-0.738,0.639-0.847,1.107l-0.619,2.658l-7.713-1.796 c-0.503-2.814-2.078-5.346-4.381-7.041l1.797-7.713l2.657,0.619c0.125,0.029,0.252,0.043,0.38,0.043 c0.348,0,0.694-0.108,0.987-0.314c0.424-0.298,0.703-0.77,0.765-1.294c0.525-4.493-2.404-8.654-6.814-9.682 c-4.406-1.028-8.874,1.413-10.389,5.676c-0.177,0.497-0.135,1.043,0.113,1.499c0.235,0.429,0.639,0.738,1.107,0.847l2.658,0.619 l-1.797,7.714c-2.812,0.504-5.344,2.078-7.038,4.38l-7.714-1.796l0.619-2.658c0.109-0.469,0.011-0.967-0.271-1.367 c-0.298-0.424-0.77-0.703-1.294-0.764c-4.498-0.526-8.654,2.405-9.682,6.814c-1.026,4.409,1.413,8.876,5.675,10.389 c0.204,0.072,0.415,0.108,0.626,0.108c0.304,0,0.604-0.075,0.873-0.222c0.43-0.235,0.738-0.638,0.847-1.107l0.619-2.658l7.71,1.795 c0.245,1.382,0.749,2.718,1.512,3.946c0.764,1.227,1.74,2.27,2.872,3.1l-1.796,7.709l-2.657-0.619 c-0.468-0.108-0.967-0.01-1.368,0.272c-0.425,0.298-0.70