insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Преодоление старости' , 'korvintag_2265' , 'Но если человек преодолеет свою усталость и проживет свою судьбу полностью — тогда его можно назвать человеком знания. Если хотя бы на одно краткое мгновение он отобьется от своего последнего, непобедимого врага! Этого мгновения ясности, силы и знания достаточно.' , 'Нужно преодолеть свою усталость, прожить свою судьбу полностью. Победить старость полностью невозможно. Здесь цель - мгновения ясности, силы и знания. Эти мгновения стоят того, чтобы жить!', (select id from users where id_out='korvintag_6'), (select id from users where name='korvin'), to_timestamp( '11.08.2023 20:23:26', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg id="Capa_1" enable-background="new 0 0 511.5 511.5" viewBox="0 0 511.5 511.5" xmlns="http://www.w3.org/2000/svg"><g id="Outline" ><path d="m344 88.25c-92.36 0-167.5 75.14-167.5 167.5s75.14 167.5 167.5 167.5 167.5-75.14 167.5-167.5-75.14-167.5-167.5-167.5zm111.616 63.706-21.163 67.163-26.941 14.001-56.012-39.937v-35.216l56.781-40.486c17.945 8.376 33.999 20.143 47.335 34.475zm-57.578 92.831-20.583 62.167h-62.841l-24.396-62.349 53.782-38.347zm-54.038-141.537c16.647 0 32.675 2.69 47.683 7.643l-47.683 33.999-47.683-33.998c15.008-4.954 31.036-7.644 47.683-7.644zm-64.281 14.231 56.781 40.486v35.216l-56.012 39.937-26.941-14.001-21.163-67.163c13.336-14.332 29.39-26.099 47.335-34.475zm-88.219 138.269c0-33.649 10.963-64.782 29.494-90.031l17.574 55.774-47.06 34.836c-.001-.193-.008-.385-.008-.579zm1.116 18.422 55.41-41.018 27.12 14.094 25.509 65.194-19.326 18.016-70.585-.63c-9.408-16.854-15.71-35.664-18.128-55.656zm27.745 70.743 58.478.522 18.593 55.533c-31.29-10.058-58.202-29.964-77.071-56.055zm123.639 63.335c-10.006 0-19.786-.979-29.259-2.828l-21.844-65.242 19.551-18.226h67.052l15.758 17.763-21.999 65.706c-9.473 1.848-19.253 2.827-29.259 2.827zm46.568-7.281 18.593-