insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Слабое и ошибающее создание' , 'korvintag_2496' , 'Одно только его общество заставило меня произвести глубокую переоценку моих моделей поведения. Я был приучен — как, пожалуй, любой другой — видеть в человеке слабое по сути своей и ошибающееся создание. Меня поражало, что дон Хуан не оставлял ни малейшего впечатления слабости и бессилия, и, просто находясь с ним рядом, я видел, что сравнение его поведения с моим будет не в мою пользу.' , 'Кастанеда как антрополог паривык, что человек - слабое и все время ошибающееся создание. Он удивился, когда увидел, что Дона Хуан принципиально иной. Получается, слабость и постоянные ошибки - не обязательный удел человека?', (select id from users where id_out='korvintag_6'), (select id from users where name='korvin'), to_timestamp( '31.08.2023 9:01:00', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 60.964 60.964" style="enable-background:new 0 0 60.964 60.964;" xml:space="preserve"><g><g><path  d="M28.639,4.168C17.696,6.194,10.05,16.3,10.709,27.413c0.012,0.205,0.037,0.398,0.064,0.593 c-0.401,1.993,0.356,4.349,2.734,5.31c3.796,1.537,8.127,3.883,9.561,7.989c1.534,4.397-1.79,9.084-4.471,12.228 c-3.54,4.15,2.451,10.201,6.02,6.021c4.831-5.667,8.262-12.039,6.884-19.653c-0.039-0.209-0.099-0.408-0.146-0.612 c0.328-0.466,0.567-1.021,0.64-1.692c0.883-8.227,3.199-16.207,4-24.446c0.651-1.4,0.852-3.021,0.461-4.465 C35.502,5.121,32.06,3.535,28.639,4.168z M26.135,31.112c-0.858-0.75-1.774-1.444-2.732-2.083c0.078-0.473,0.107-0.98,0.077-1.527 c-0.038-0.632-0.009-1.267,0.029-1.896c0.016-0.222,0.036-0.44,0.058-0.658c0.01-0.059,0.021-0.12,0.037-0.202 c0.152-0.853,0.386-1.688,0.645-2.513c0.111-0.297,0.17-0.461,0.18-0.508c0.191-0.402,0.401-0.795,0.618-1.184 c0.708-1.272,0.314-0.