insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Что нам нужно от жизни?' , 'korvintag_3850' , 'Следующие пять месяцев я прожил в состоянии непрерывного глубокого покоя и блаженства. Затем эти ощущения немного потеряли яркость; а может быть, мне так только казалось, потому что я к ним привык. Я по-прежнему мог функционировать в обществе, хотя и сознавал: всё, что я делаю, не способно ничего прибавить к тому, что у меня уже есть.' , 'То, что нам реально нужно, не в состоянии дать никакая деятельность в обществе. То, что есть у нас, но скрыто из-за настроек нашего разума, есть высшая ценность.', (select id from users where id_out='korvintag_109'), (select id from users where name='korvin'), to_timestamp( '27.12.2023 18:28:02', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 512 512" style="enable-background:new 0 0 512 512;" xml:space="preserve"><g><g><path d="M500.85,285.719C506.076,277.013,512,267.147,512,256c0-11.147-5.924-21.013-11.15-29.719 c-3.696-6.157-7.187-11.972-8.25-17.347c-1.126-5.693-0.106-12.586,0.974-19.885c1.535-10.369,3.123-21.091-1.004-31.043 c-4.209-10.151-13.013-16.661-21.526-22.955c-5.84-4.319-11.358-8.398-14.479-13.059c-3.169-4.735-4.854-11.442-6.637-18.544 c-2.565-10.214-5.217-20.775-12.908-28.467c-7.692-7.692-18.254-10.344-28.468-12.909c-7.101-1.784-13.809-3.468-18.543-6.638 c-4.661-3.12-8.74-8.637-13.059-14.478c-6.294-8.514-12.804-17.317-22.955-21.526c-4.256-1.764-9.037-2.622-14.616-2.622 c-5.498,0-11.055,0.822-16.427,1.617c-5.079,0.751-9.876,1.462-14.303,1.462c-2.097,0-3.923-0.16-5.581-0.488 c-5.374-1.063-11.189-4.554-17.345-8.25C277.014,5.924,267.147,0,256,0c-11.147,0-21.014,5.924-29.72,11.15 c-6.156,3.696-11.971,7.187-17.345,8.25c-1.659,0.328-3.485,0.488-5.581,0.488c-4.428,0-9.226-0.71-14.303-1.462 c-5.374-0.795-10