insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Петля обратной связи' , 'korvintag_4740' , 'После того как вы получите неожиданное вознаграждение, вы измените свою стратегию на будущее. Мозг незамедлительно начнет фиксировать те события, которые предшествовали вознаграждению. Погоди-ка – это было приятно. Что я делал непосредственно перед этим моментом?
Это и есть та самая петля обратной связи, которая стоит за всеми поступками человека: пробовать, ошибаться, учиться, пробовать иным образом. С практикой бесполезные действия прекращаются, а полезные действия закрепляются. Это и есть формирование привычки.' , 'Привычки формируются под воздействием т.н. петли обратной связи. Когда мы что-то сделали удачное, наш мозг получает гормоны. А гормоны заставляют мозг запомнить хорошее и сформировать новую привычку.', (select id from users where id_out='korvintag_24'), (select id from users where name='korvin'), to_timestamp( '12.03.2024 21:42:59', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 46.243 46.243" style="enable-background:new 0 0 46.243 46.243;" xml:space="preserve"><g><path d="M36.012,0.001h-25.78c-5.651,0-10.231,4.561-10.231,10.211v9.897v3.115c0,1.607,1.303,2.91,2.91,2.91h10.292h5.278h0.016 v7.236c0,0.889,0.529,1.691,1.351,2.029c0.822,0.34,1.765,0.15,2.392-0.479l9.708-9.758c1.134-1.14,1.133-2.982,0-4.123 l-9.71-9.756c-0.627-0.632-1.565-0.821-2.387-0.482c-0.823,0.34-1.352,1.141-1.352,2.031v7.276h-0.017H6.032H6.026v-9.897 c0-2.316,1.89-4.186,4.206-4.186h25.78c2.316,0,4.207,1.87,4.207,4.186v25.78c0,2.314-1.891,4.226-4.207,4.226h-25.78 c-2.316,0-4.206-1.909-4.206-4.226v-0.819c0-1.664-1.349-3.013-3.013-3.013S0,33.508,0,35.172v0.819 c0,5.65,4.581,10.251,10.231,10.251h25.781c5.65,0,10.231-4.601,10.231-10.251v-25.78C46.243,4.562,41.662,0.001,36.012,0.001z"/></g></svg>' where not exists (select * from ideas where id_out='korvintag_4740');
