insert into ideas (name, id_out, original_text, content, user_id, moderated, date_time_create,"SVG") select 'Зачем мозг формирует привычки?' , 'korvintag_4782' , 'Привычка – это поведение, которое повторилось достаточное количество раз для того, чтобы стать автоматическим. Процесс ее формирования начинается со стадии проб и ошибок. Каждый раз, когда вы сталкиваетесь с новой жизненной ситуацией, мозгу приходится принимать решение. Как реагировать на эту ситуацию? Когда вы впервые сталкиваетесь с проблемой, то не уверены в том, каким образом ее следует решать. Подобно кошке Торндайка, вы сначала испытываете все предметы, чтобы проверить, что сработает.' , 'Привычки нужны нашему мозгу, чтобы экономить время и ресурсы при решении регулярно возникаемых похожих проблем.', (select id from users where id_out='korvintag_24'), (select id from users where name='korvin'), to_timestamp( '16.03.2024 10:16:18', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 511 511" xmlns:xlink="http://www.w3.org/1999/xlink" enable-background="new 0 0 511 511"><path d="m487,311.5c0-23.401-12.708-44.564-32.894-55.642 0.594-2.735 0.894-5.528 0.894-8.358 0-5.807-1.299-11.521-3.789-16.776 7.728-11.692 11.789-25.153 11.789-39.224 0-24.173-12.234-46.596-32.266-59.76 0.179-1.442 0.266-2.842 0.266-4.24 0-12.456-6.029-24.172-16.002-31.58 0.001-0.14 0.002-0.279 0.002-0.42 0-28.87-19.654-54.102-47.249-61.389-10.9-20.84-32.581-34.111-56.251-34.111-24.209,0-45.29,13.622-56,33.599-10.71-19.977-31.791-33.599-56-33.599-23.67,0-45.351,13.271-56.251,34.111-27.595,7.287-47.249,32.519-47.249,61.389 0,0.141 0.001,0.28 0.002,0.42-9.973,7.408-16.002,19.124-16.002,31.58 0,1.398 0.087,2.797 0.266,4.24-20.032,13.164-32.266,35.587-32.266,59.76 0,14.071 4.061,27.532 11.789,39.224-2.49,5.255-3.789,10.969-3.789,16.776 0,2.83 0.299,5.623 0.894,8.358-20.186,11.078-32.894,32.241-32.894,55.642 0,27.946 18.411,52.263 44.301,60.48-2.83