— Ответ очень прост. Он не должен убегать. Он должен победить свой страх и вопреки ему сделать следующий шаг в учении, еще шаг и еще. Будучи всецело во власти страха, он все же не должен останавливаться. Таково правило! И придет миг, когда его первый враг отступит. Человек начнет чувствовать уверенность в себе. Его намерение крепнет. Учение — уже не пугающая задача. В этот радостный миг человек может сказать без колебаний, что он победил своего первого природного врага.

— Это произойдет сразу, дон Хуан, или мало-помалу?
— Это произойдет мало-помалу, но страх исчезнет внезапно и быстро.' , 'При столкновении со страхом нужно идти дальше, невзирая на него. Даже если полностью находишься под его влиянием. И он уйдет. И появится уверенность.', (select id from users where id_out='korvintag_6'), (select id from users where name='korvin'), to_timestamp( '10.04.2025 10:52:46', 'DD.MM.YYYY HH24:MI:SS'), '<!-- icon666.com - MILLIONS OF FREE VECTOR ICONS --><svg version="1.1" id="Capa_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 125.09 125.09" style="enable-background:new 0 0 125.09 125.09;" xml:space="preserve"><g><g><circle cx="66.384" cy="13.038" r="13.038"/><path d="M79.029,112.697c0.767-0.67,1.382-1.53,1.731-2.563c0.128-0.375,3.113-9.289,2.15-17.989 c-0.559-5.051-4.77-11.166-9.028-16.187c0.72-0.315,1.379-0.792,1.905-1.446c5.575-6.916,7.968-13.995,9.553-18.679 c0.23-0.685,0.447-1.326,0.658-1.916c1.258-3.53,1.177-9.793-13.989-23.375c-0.189-0.17-0.398-0.306-0.606-0.442 c-1.254-1.214-2.732-2.209-4.296-2.908l-3.781,2.635l-2.505-3.817c-0.012,0.001-0.022,0-0.033,0 c-1.743,0.095-3.511,0.513-5.141,1.224c-0.239,0.055-0.479,0.106-0.714,0.198c-6.899,2.675-17.324,6.717-20.486,14.987 c-0.116,0.304-0.202,0.618-0.259,0.939c-0.167,0.957-0.447,2.208-0.771,3.657c-0.867,3.87-2.056,9.169-2.693,15.83 c-0.266,2.783,1.772,5.255,4.556,5.521c0.164,0.016,0.327,0.022,0.489,0.022c2.578,0,4.781-1.96,5.032-4.578 c0.578-6.031,1.686-10.975,2.494-14.583c0.297-1.323,0.559-2.494,0.759-3.542c0.61-1.261,1.879-2.445,3.462-3.529l-3.922,21.521 c-0.356,1.953-0.131,3.814,0.513,5.512l-12.494,48.422c-0.824,3.199,1.1,6.461,4.298,7.287c0.501,0.129,1.004,0.191,1.499,0.191 c2.662,0,5.092-1.792,5.787-4.488L54.365,77.32c0.1,0.021,0.196,0.05,0.297,0.067c1.446,0.263,2.911,0.302,4.338,0.151 c5.736,5.662,11.706,13.078,12.021,15.919c0.665,6.009-1.567,12.767-1.586,12.821c-0.803,2.356-0.049,4.867,1.711,6.416H54.508 v11.964h39.88v-11.964H79.029V112.697z M62.596,51.547l-3.864,3.67l-0